mapper.select:
SELECT profile.profile_id, profile.user_id, profile.profile_image_url, profile.profile_intro, profile.profile_contents, profile.profile_modify_date, ( SELECT GROUP_CONCAT(k.keyword_name) FROM t_m_user_keyword muk INNER JOIN t_keyword k ON muk.keyword_id = k.keyword_id WHERE muk.user_id = profile.user_id GROUP BY muk.user_id ) AS keyword_info, account.user_grade, account.user_nick_name FROM t_profile as profile INNER JOIN t_user_account account ON profile.user_id = account.user_id INNER JOIN t_m_user_keyword muk ON profile.user_id = muk.user_id INNER JOIN t_keyword k ON muk.keyword_id = k.keyword_id
( profile.profile_intro LIKE CONCAT('%', #{condition.search}, '%') OR profile.profile_contents LIKE CONCAT('%', #{condition.search}, '%') OR account.user_nick_name LIKE CONCAT('%', #{condition.search}, '%') )
#{keyword}
#{userGrade}
AND profile.profile_active_flag = 1
GROUP BY profile.profile_id
ORDER BY
${order.property} ${order.direction}
LIMIT #{pageable.pageSize} OFFSET #{pageable.offset}

SELECT COUNT(DISTINCT profile.profile_id) FROM t_profile as profile inner JOIN t_user_account as account ON profile.user_id = account.user_id inner JOIN t_m_user_keyword muk ON profile.user_id = muk.user_id
( profile.profile_intro LIKE CONCAT('%', #{search}, '%') OR profile.profile_contents LIKE CONCAT('%', #{search}, '%') OR account.user_nick_name LIKE CONCAT('%', #{search}, '%') )
#{keyword}
#{userGrade}
AND profile.profile_active_flag = 1

mapper.update:
UPDATE t_user_account
SET
    user_nick_name = #{request.userNickName},
    user_grade = #{request.userGrade}
WHERE
    user_id = #{userId}
